			LUI.FlowManager.RequestPopupMenu( nil, "campaign_mode_savedata_error_popup" )
		end
	end
end

LUI.HUD.RegisterOmnvarMenuOpenHandler = function ( f5_arg0, f5_arg1, f5_arg2, f5_arg3 )
	f5_arg0:registerOmnvarHandler( f5_arg1, function ( f6_arg0, f6_arg1 )
		if f6_arg1.value then
			LUI.FlowManager.RequestAddMenu( f5_arg0, false, nil, false )
			f6_arg0:UpdateWidgetsVisibility()
		end
	end )
	if f5_arg3 and Game.BFEEGCHICA( f5_arg0.controllerIndex, f5_arg1 ) then
		LUI.FlowManager.RequestAddMenu( f5_arg2, false, nil, false )
	end
end

LUI.HUD.processEvent = function ( f4_arg0, f4_arg1 )
	if IsInputEvent( f4_arg1.name ) then
		return false
	else
		return LUI.UIElement.processEvent( f4_arg0, f4_arg1 )
	end
end

LUI.HUD.AllowChatInHUD = function ()
	LUI.HUD.AllowChatInHUDCounter = LUI.HUD.AllowChatInHUDCounter + 1
end

LUI.HUD.DisallowChatInHUD = function ()
	if 0 < LUI.HUD.AllowChatInHUDCounter then
		LUI.HUD.AllowChatInHUDCounter = LUI.HUD.AllowChatInHUDCounter - 1
	end
end

LUI.HUD.IsChatAllowedInHUD = function ()
	return 0 < LUI.HUD.AllowChatInHUDCounter
end

