		f3_local1 = f3_arg1.textfield.setText
	end
	f3_local0( f3_local1, "You must specify a valid textfield" )
	assert( f3_arg1.textfieldBackingImage, "You must specify a valid image element." )
	f3_local0 = 0
	if f3_arg1.padding then
		assert( type( f3_arg1.padding ) == "number", "You must specify a valid number for the padding." )
		f3_local0 = _1080p * f3_arg1.padding
