	local f3_local2 = LAYOUT.GetTextWidth( f3_arg1.textfield )
	if 0 < f3_local2 then
		f3_local1 = f3_arg1.textfield:GetCurrentAnchorsAndPositions()
		local f3_local3 = f3_arg1.textfieldBackingImage:GetCurrentAnchorsAndPositions()
		if LAYOUT.IsElementAnchoredRight( f3_arg1.textfieldBackingImage ) or f3_arg1.forceAlignRight then
