		f1_local2:addEventHandler( "button_action", function ( f3_arg0, f3_arg1 )
			ACTIONS.OpenPopupMenu( menu, "TrialRetryPopup", true, f3_arg1.controller or menu )
			return true
		end )
	end
	if CONDITIONS.IsTeamChoiceAllowed() then
		f1_local5:addEventHandler( "button_action", function ( f2_arg0, f2_arg1 )
			ACTIONS.OpenMenu( "TeamSelectMenu", true, f2_arg1.controller or menu )
			return true
		end )
	end
	f0_local0( self, f1_local1, controller )
	if CONDITIONS.IsArabic() then
		ACTIONS.AnimateSequence( self, "AR", controller )
	end
	return self
end

MenuBuilder.registerType( "PauseMenuButtons", PauseMenuButtons )
LockTable( _M )
